import React, { useEffect, useState } from "react";

export default function FeaturedProperty() {
  const [properties, setProperties] = useState([]);

  useEffect(() => {
    fetch("https://68b826bcb715405043274639.mockapi.io/api/properties/PropertyListing")
      .then((res) => res.json())
      .then((data) => setProperties(data.slice(0, 4))) // take first 4
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="container my-5">
      {/* Heading + Button */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="fw-bold">Featured Property</h2>
        <button className="btn btn-outline-primary">See 268 new projects</button>
      </div>

      {/* Property Grid */}
      <div className="row g-3">
        {/* First Property - Tall */}
        <div className="col-md-5">
          {properties[0] && (
            <div className="h-100">
              <img
                src={properties[0].image}
                alt={properties[0].name}
                className="img-fluid w-200 h-100 rounded object-fit-cover"
                style={{ minHeight: "600px" }}
              />
            </div>
          )}
        </div>

        {/* Second Property - Wide */}
        <div className="col-md-4">
          {properties[1] && (
            <div>
              <img
                src={properties[1].image}
                alt={properties[1].name}
                className="img-fluid w-100 rounded object-fit-cover"
                style={{ height: "200px" }}
              />
            </div>
          )}

          {/* Third + Fourth stacked */}
          <div className="row mt-3 g-3">
            <div className="col-12">
              {properties[2] && (
                <img
                  src={properties[2].image}
                  alt={properties[2].name}
                  className="img-fluid w-100 rounded object-fit-cover"
                  style={{ height: "150px" }}
                />
              )}
            </div>
            <div className="col-12">
              {properties[3] && (
                <img
                  src={properties[3].image}
                  alt={properties[3].name}
                  className="img-fluid w-100 rounded object-fit-cover"
                  style={{ height: "150px" }}
                />
              )}
            </div>
          </div>
        </div>

        {/* Empty column for spacing */}
        <div className="col-md-4"></div>
      </div>
    </div>
  );
}
