import React, { useState } from "react";
import logo from "../assets/logo.png"; // replace with your logo

const Footer = () => {
  const [email, setEmail] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!email) {
      alert("Please fill out this form");
    } else {
      alert(`Thank you for subscribing with ${email}`);
      setEmail("");
    }
  };

  return (
    <footer style={{ backgroundColor: "#1c3f6c", color: "white", padding: "50px 20px" }}>
      
      {/* Center subscription */}
      <div style={{ textAlign: "center", marginBottom: "50px" }}>
        <h1>Get in touch with us</h1>
        <h2>Subscribe now for exclusive property insights and deals</h2>

        {/* Input with button inside */}
        <form onSubmit={handleSubmit} style={{ marginTop: "20px" }}>
          <div style={{ position: "relative", width: "300px", margin: "0 auto" }}>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={{
                width: "110%",
                padding: "12px 10px",
                paddingRight: "100px", // space for button
                borderRadius: "50px",
                border: "none",
                fontSize: "16px",
              }}
            />
            <button
              type="submit"
              style={{
                position: "absolute",
                right: "-27px",
                top: "4px",
                height: "80%",
                border: "none",
                borderRadius: "50px",
                backgroundColor: "#0d6efd",
                color: "white",
                padding: "0 20px",
                cursor: "pointer",
              }}
            >
              Subscribe
            </button>
          </div>
        </form>
      </div>

      {/* Bottom section */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", borderTop: "1px solid rgba(255,255,255,0.3)", paddingTop: "20px" }}>
        <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
          <img src={logo} alt="Logo" style={{ width: "40px", height: "40px" }} />
          <span style={{ fontWeight: "bold", fontSize: "18px" }}>Prop Boot</span>
        </div>

        <div style={{ display: "flex", gap: "20px" }}>
          <a href="#" style={{ color: "white", textDecoration: "none" }}>For Sale</a>
          <a href="#" style={{ color: "white", textDecoration: "none" }}>Rentals</a>
          <a href="#" style={{ color: "white", textDecoration: "none" }}>New Projects</a>
          <a href="#" style={{ color: "white", textDecoration: "none" }}>Property News</a>
        </div>

        <div style={{ fontSize: "14px" }}>
          © 2025 Prop Boot. All rights reserved.
        </div>
      </div>
    </footer>
  );
};

export default Footer;
