import React, { useEffect, useState } from "react";
import { MapPin, Star } from "lucide-react";

const Bestproperties = () => {
  const [properties, setProperties] = useState([]);

  useEffect(() => {
    fetch("https://68b826bcb715405043274639.mockapi.io/api/properties/PropertyListing")
      .then((res) => res.json())
      .then((data) => setProperties(data.slice(0, 4))) // first 4 only
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="px-6 py-12 max-w-7xl mx-auto">
      {/* Heading + Button Row */}
      <div className="flex flex-col md:flex-row md:justify-between md:items-center mb-2 gap-4">
        <h2 className="text-2xl font-bold text-blue-700">
          Best Properties Available For Sale
        </h2>
        <button className="bg-blue-800 text-white px-6 py-2 rounded-full font-medium hover:bg-blue-900 transition w-max">
          View More Properties
        </button>
      </div>

      {/* Subheading */}
      <div className="mb-8">
        <p className="text-gray-600">
          Browse our top-rated properties for sale, featuring premium listings
          tailored to your needs.
        </p>
        <p className="text-gray-600">Find your dream home today!</p>
      </div>

      {/* Responsive Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {properties.map((property, index) => (
          <div
            key={index}
            className="bg-white rounded-xl shadow-md hover:shadow-xl transition transform hover:scale-105 flex-shrink-0"
          >
            {/* Image */}
            <img
              src={property.image}
              alt={property.name}
              className="h-44 w-full object-cover rounded-t-xl"
            />

            {/* Card Content */}
            <div className="p-4 flex flex-col">
              {/* Location */}
              <div className="flex items-center text-gray-500 text-sm mb-2">
                <MapPin className="h-4 w-4 mr-1 text-blue-700" />
                {property.city}, {property.state}
              </div>

              {/* Rating */}
              <div className="flex items-center text-yellow-500 mb-2">
                <Star className="h-4 w-4 mr-1 fill-yellow-500" />
                <span className="text-sm font-medium">
                  {(4 + Math.random()).toFixed(1)} / 5
                </span>
              </div>

              {/* Description */}
              <p className="text-gray-700 text-sm line-clamp-3 mb-4">
                Beautiful {property.name} located in {property.city},{" "}
                {property.state}. Modern amenities and prime location.
              </p>

              {/* Buy Button + Price */}
              <div className="mt-auto flex items-center justify-between">
                <button className="bg-blue-800 text-white px-4 py-2 rounded-lg hover:bg-blue-900 transition text-sm font-medium">
                  Buy Now
                </button>
                <span className="text-lg font-bold text-gray-800">
                  ${Math.floor(Math.random() * 100000 + 200000).toLocaleString()}
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BestProperties;
