import React, { useEffect, useState } from "react";

export default function FeaturedProperty() {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(
      "https://68b826bcb715405043274639.mockapi.io/api/properties/PropertyListing"
    )
      .then((res) => res.json())
      .then((data) => {
        setProperties(data.slice(0, 4)); // first 4 properties
        setLoading(false);
      })
      .catch((err) => {
        console.error("Fetch error:", err);
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="text-center py-5">Loading...</div>;

  return (
    <div className="container my-5">
      {/* Header: right aligned */}
      <div className="d-flex justify-content-end align-items-center mb-3 gap-3">
        <h3 className="mb-0">Featured Property</h3>
        <button className="btn btn-primary rounded-pill px-4">
          See 268 new projects <span className="ms-2">â†’</span>
        </button>
      </div>

      {/* Grid: left large, middle tall, right stacked */}
      <div className="featured-grid">
        {/* Large left (first property) */}
        <div className="item large">
          {properties[0] ? (
            <img
              src={properties[0].image}
              alt={properties[0].name}
              loading="lazy"
            />
          ) : (
            <div className="placeholder">No image</div>
          )}
        </div>

        {/* Middle tall (second property) */}
        <div className="item middle">
          {properties[1] ? (
            <img
              src={properties[1].image}
              alt={properties[1].name}
              loading="lazy"
            />
          ) : (
            <div className="placeholder">No image</div>
          )}
        </div>

        {/* Right column: two stacked images (third and fourth) */}
        <div className="right-col">
          <div className="item small top">
            {properties[2] ? (
              <img
                src={properties[2].image}
                alt={properties[2].name}
                loading="lazy"
              />
            ) : (
              <div className="placeholder">No image</div>
            )}
          </div>
          <div className="item small bottom">
            {properties[3] ? (
              <img
                src={properties[3].image}
                alt={properties[3].name}
                loading="lazy"
              />
            ) : (
              <div className="placeholder">No image</div>
            )}
          </div>
        </div>
      </div>

      {/* Styles: you can move this to your CSS file (e.g. App.css) */}
      <style>{`
        .featured-grid {
          display: grid;
          grid-template-columns: 30% 40% 30%;
          grid-template-rows: 1fr 1fr;
          gap: 16px;
          align-items: stretch;
          height: 520px; /* adjust as needed */
        }

        .featured-grid .item img {
          width: 100%;
          height: 100%;
          object-fit: cover;
          border-radius: 10px;
          display: block;
        }

        .featured-grid .placeholder {
          width: 100%;
          height: 100%;
          background: #f0f0f0;
          border-radius: 10px;
          display:flex;
          align-items:center;
          justify-content:center;
          color:#666;
        }

        /* make left + middle span full height */
        .featured-grid .large {
          grid-row: 1 / span 2;
        }
        .featured-grid .middle {
          grid-row: 1 / span 2;
        }

        /* right column is its own two-row grid */
        .right-col {
          display: grid;
          grid-template-rows: 1fr 1fr;
          gap: 16px;
        }

        /* small items (third + fourth) get equal height */
        .right-col .item { overflow: hidden; }

        /* Responsive: stack on small screens */
        @media (max-width: 992px) {
          .featured-grid {
            grid-template-columns: 1fr;
            grid-template-rows: auto;
            height: auto;
          }
          .featured-grid .large,
          .featured-grid .middle { grid-row: auto; }
          .right-col { grid-template-rows: none; grid-auto-rows: 260px; }
          .featured-grid .item img { height: 260px; }
          .d-flex.justify-content-end { justify-content: center; gap: 10px; }
        }

        /* small tweak for very small screens */
        @media (max-width: 576px) {
          .featured-grid .item img { height: 220px; }
        }
      `}</style>
    </div>
  );
}
