// JourneyForm.js
import React, { useState } from "react";

const JourneyForm = () => {
  const [userType, setUserType] = useState("");

  const handleUserTypeChange = (e) => setUserType(e.target.value);

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Form submitted!");
  };

  return (
    <div className="p-6 max-w-4xl mx-auto font-sans">
      {/* Add padding-left to align heading with inputs */}
      <div className="pl-4 md:pl-0">
        <h2 className="text-3xl font-bold text-blue-600 mb-2">
          Start Your Journey Today
        </h2>
        <p className="text-gray-700 mb-6">
          Create a profile in seconds and find your ideal home
        </p>
      </div>

      {/* Form */}
      <form
        className="flex flex-wrap gap-4 items-end pl-4 md:pl-0"
        onSubmit={handleSubmit}
      >
        {/* Name */}
        <input
          type="text"
          placeholder="Enter your name"
          className="border border-gray-300 rounded px-3 py-2 flex-1 min-w-[150px]"
        />

        {/* User Type Dropdown */}
        <select
          value={userType}
          onChange={handleUserTypeChange}
          className="border border-gray-300 rounded px-3 py-2 flex-1 min-w-[150px]"
        >
          <option value="">Select user type</option>
          <option value="buyer">Buyer</option>
          <option value="seller">Seller</option>
        </select>

        {/* Location */}
        <input
          type="text"
          placeholder="Enter your location"
          className="border border-gray-300 rounded px-3 py-2 flex-1 min-w-[150px]"
        />

        {/* Continue Button */}
        <button
          type="submit"
          className="bg-blue-600 text-white px-5 py-2 rounded hover:bg-blue-700 transition flex-shrink-0"
        >
          Continue
        </button>
      </form>
    </div>
  );
};

export default JourneyForm;
