import React, { useEffect, useState } from "react";
import { MapPin, Star } from "lucide-react";

const BestProperties = () => {
  const [properties, setProperties] = useState([]);

  useEffect(() => {
    fetch("https://68b826bcb715405043274639.mockapi.io/api/properties/PropertyListing")
      .then((res) => res.json())
      .then((data) => setProperties(data.slice(0, 4))) // only 4 properties
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="px-8 py-12 max-w-7xl mx-auto">
      {/* Heading + Button in one row */}
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold text-blue-600">
          Best Properties Available
        </h2>
        <button className="bg-blue-600 text-white px-5 py-2 rounded-full font-medium hover:bg-blue-700 transition">
          View More Properties
        </button>
      </div>

      {/* Subheading below heading */}
      <div className="mb-8">
        <p className="text-gray-600">
          Explore top-rated properties tailored just for you.
        </p>
        <p className="text-gray-600">
          Verified listings with excellent value and trusted owners.
        </p>
      </div>

      {/* 4 Property Cards in one row */}
      <div className="flex gap-6 overflow-x-auto">
        {properties.map((property, index) => (
          <div
            key={index}
            className="bg-white rounded-xl shadow-md hover:shadow-xl transition flex-shrink-0 w-72"
          >
            {/* Image */}
            <img
              src={property.image}
              alt={property.name}
              className="h-48 w-full object-cover rounded-t-xl"
            />

            {/* Card Content */}
            <div className="p-4 flex flex-col">
              {/* Location */}
              <div className="flex items-center text-gray-500 text-sm mb-2">
                <MapPin className="h-4 w-4 mr-1 text-blue-600" />
                {property.city}, {property.country}
              </div>

              {/* Rating */}
              <div className="flex items-center text-yellow-500 mb-2">
                <Star className="h-4 w-4 mr-1 fill-yellow-500" />
                <span className="text-sm font-medium">4.7 / 5</span>
              </div>

              {/* Description */}
              <p className="text-gray-700 text-sm line-clamp-3 mb-4">
                {property.name} located at {property.buildingNumber}{" "}
                {property.cardinalDirection}, {property.state}. Owner:{" "}
                {property.ownerName}.
              </p>

              {/* Buy Button + Price */}
              <div className="mt-auto flex items-center justify-between">
                <button className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition text-sm font-medium">
                  Buy Now
                </button>
                <span className="text-lg font-bold text-gray-800">
                  ${Math.floor(Math.random() * 500 + 100)}k
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BestProperties;
