import React, { useEffect, useState } from "react";
import { MapPin, Star } from "lucide-react";

const BestProperties = () => {
  const [properties, setProperties] = useState([]);

  useEffect(() => {
    fetch("https://68b826bcb715405043274639.mockapi.io/api/properties/PropertyListing")
      .then((res) => res.json())
      .then((data) => setProperties(data.slice(0, 4))) // only 4
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="px-6 py-10 max-w-7xl mx-auto">
      {/* Header */}
      <div className="flex justify-between items-start mb-6 flex-wrap gap-4">
        <div>
          <h2 className="text-2xl font-bold text-blue-600">
            Best Properties Available
          </h2>
          <p className="text-gray-600 mt-2 max-w-md">
            Explore top-rated properties tailored for you.
          </p>
          <p className="text-gray-600">
            Hand-picked selections with excellent value.
          </p>
        </div>
        <button className="bg-blue-600 text-white px-4 py-2 rounded-full font-medium hover:bg-blue-700 transition">
          View More Properties
        </button>
      </div>

      {/* Properties */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {properties.map((property, index) => (
          <div
            key={index}
            className="bg-white rounded-xl shadow-md hover:shadow-xl transition overflow-hidden flex flex-col"
          >
            <img
              src={property.image}
              alt={property.name}
              className="h-48 w-full object-cover"
            />

            <div className="p-4 flex-1 flex flex-col">
              {/* Location */}
              <div className="flex items-center text-gray-500 text-sm mb-2">
                <MapPin className="h-4 w-4 mr-1 text-blue-600" />
                {property.city}, {property.country}
              </div>

              {/* Rating */}
              <div className="flex items-center text-yellow-500 mb-2">
                <Star className="h-4 w-4 mr-1 fill-yellow-500" />
                <span className="text-sm font-medium">4.7 / 5</span>
              </div>

              {/* Description */}
              <p className="text-gray-700 text-sm line-clamp-3 flex-1">
                {property.name} located at {property.buildingNumber}{" "}
                {property.cardinalDirection} {property.state}. Owned by{" "}
                {property.ownerName}.
              </p>

              {/* Footer */}
              <div className="mt-4 flex items-center justify-between">
                <button className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition text-sm font-medium">
                  Buy Now
                </button>
                <span className="text-lg font-bold text-gray-800">
                  ${Math.floor(Math.random() * 500 + 100)}k
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BestProperties;
