import React, { useState, useEffect } from "react";

const FeaturedProperties = () => {
  const [properties, setProperties] = useState([]);
  const [clicked, setClicked] = useState(false);

  useEffect(() => {
    fetch("https://68b826bcb715405043274639.mockapi.io/api/properties/PropertyListing")
      .then((res) => res.json())
      .then((data) => setProperties(data))
      .catch((err) => console.error(err));
  }, []);

  const handleButtonClick = () => setClicked(!clicked);

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      {/* Top bar */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "20px" }}>
        <h2 style={{ margin: 0 }}>Featured Property</h2>
        <button
          onClick={handleButtonClick}
          style={{
            padding: "10px 20px",
            backgroundColor: clicked ? "blue" : "#eee",
            color: clicked ? "#fff" : "#000",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
            display: "flex",
            alignItems: "center",
            gap: "5px",
          }}
        >
          See 268 new projects ‚û°
        </button>
      </div>

      {/* Properties grid */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: "15px" }}>
        {properties.slice(0, 25).map((property) => (
          <div
            key={property.id}
            style={{
              position: "relative",
              width: "calc(50% - 15px)",
              borderRadius: "10px",
              overflow: "hidden",
              boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
              marginBottom: "20px",
            }}
          >
            <img
              src={property.image}
              alt={property.name}
              style={{ width: "100%", height: "250px", objectFit: "cover" }}
            />

            {/* Overlay div */}
            <div
              style={{
                position: "absolute",
                bottom: "0",
                left: "0",
                width: "100%",
                background: "rgba(0,0,0,0.6)",
                color: "#fff",
                padding: "10px",
                display: "flex",
                flexDirection: "column",
                gap: "5px",
              }}
            >
              <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                <div style={{ display: "flex", alignItems: "center", gap: "5px" }}>
                  <span>üìç</span>
                  <span>{property.city}, {property.state}</span>
                </div>
                <div>üíæ</div>
              </div>
              <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                <div>üí≤ {property.buildingNumber}</div>
                <button style={{ padding: "5px 10px", borderRadius: "5px", border: "none", cursor: "pointer" }}>
                  No More
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FeaturedProperties;
